let menu=document.querySelector(".menu"),headerButton=document.querySelector(".header__button");headerButton.addEventListener("click",()=>{menu.classList.toggle("menu--active")});const anchors=document.querySelectorAll('a[href^="#"]');for(let e of anchors)e.addEventListener("click",t=>{t.preventDefault();const o=e.getAttribute("href");document.querySelector(" "+o).scrollIntoView({behavior:"smooth",block:"start"})});